  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'
  import { Playground } from 'docz';
import { useClickOutside } from '../src';

const layoutProps = {

};
export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}>
<MDXTag name="h1" components={components} props={{"id":"useclickoutside"}}>{`useClickOutside`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-js"}}>{`import { useClickOutside } from 'use-events';
`}</MDXTag></MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-jsx"}}>{`const Example = () => {
  const ref1 = React.useRef(null);
  const ref2 = React.useRef(null);
  const [isActive] = useClickOutside([ref1, ref2], event => console.log(event));

  return (
    <div>
      <div ref={ref1} style={{ border: '1px dotted black' }}>
        You are {isActive ? 'clicking' : 'not clicking'} outside of this div
      </div>
      <br />
      <div ref={ref2} style={{ border: '1px dotted black' }}>
        You are {isActive ? 'clicking' : 'not clicking'} outside of this div
      </div>
    </div>
  );
