  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


const layoutProps = {

};
export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><MDXTag name="h1" components={components} props={{"id":"introduction"}}>{`Introduction`}</MDXTag>
<MDXTag name="p" components={components}><MDXTag name="strong" components={components} parentName="p">{`Use Events`}</MDXTag>{` - Events turned into React Hooks.
Read about `}<MDXTag name="a" components={components} parentName="p" props={{"href":"https://reactjs.org/docs/hooks-intro.html"}}>{`Hooks`}</MDXTag>{` feature.`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"install"}}>{`Install`}</MDXTag>
<MDXTag name="blockquote" components={components}>
<MDXTag name="p" components={components} parentName="blockquote">{`Note: React 16.8+ is required for Hooks.`}</MDXTag>
</MDXTag>
<MDXTag name="h3" components={components} props={{"id":"with-npm"}}>{`With npm`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-sh"}}>{`npm i use-events
`}</MDXTag></MDXTag>
<MDXTag name="h3" components={components} props={{"id":"or-with-yarn"}}>{`Or with yarn`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-sh"}}>{`yarn add use-events
`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`exposed as `}<MDXTag name="inlineCode" components={components} parentName="p">{`UseEvents`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"quick-examples"}}>{`Quick Examples`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-jsx"}}>{`import { useActive, useHover, useMousePosition } from 'use-events';
`}</MDXTag></MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-jsx"}}>{`const App = () => {
  const [isActive, bind] = useActive();

  return (
    <button style={{ color: isActive ? 'white' : 'black' }} {...bind}>
      Click me
    </button>
  );
};
`}</MDXTag></MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-jsx"}}>{`const App = () => {
  const [isHovered, hoverBind] = useHover();
  const [x, y, mousePositionBind] = useMousePosition();

  return (
    <div {...hoverBind} {...mousePositionBind}>
      {isHovered && (
        <span>
          {x} - {y}
        </span>
      )}
    </div>
  );
};
`}</MDXTag></MDXTag>
           </MDXTag>
  }
}
